import React, { useState, useEffect } from 'react';

function App() {
  const [gameState, setGameState] = useState(null);
  const [paddle1Y, setPaddle1Y] = useState(200);
  const [paddle2Y, setPaddle2Y] = useState(200);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8000/ws');
    setSocket(ws);

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setGameState(data);
    };

    return () => {
      ws.close();
    };
  }, []);

  const handlePaddle1Move = (e) => {
    setPaddle1Y(e.target.value);
    socket.send(JSON.stringify({ paddle1: { y: parseInt(e.target.value) } }));
  };

  const handlePaddle2Move = (e) => {
    setPaddle2Y(e.target.value);
    socket.send(JSON.stringify({ paddle2: { y: parseInt(e.target.value) } }));
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Game</h1>
      {gameState && (
        <>
          <div>Score: Player 1 - {gameState.score.player1} | Player 2 - {gameState.score.player2}</div>
          <div style={{ marginTop: 50, height: '400px', width: '600px', border: '1px solid black', position: 'relative' }}>
            <div
              style={{
                position: 'absolute',
                top: gameState.ball.y,
                left: gameState.ball.x,
                width: 10,
                height: 10,
                backgroundColor: 'red',
                borderRadius: '50%',
              }}
            ></div>
            <div
              style={{
                position: 'absolute',
                top: gameState.paddle1.y,
                left: 50,
                width: 10,
                height: 50,
                backgroundColor: 'blue',
              }}
            ></div>
            <div
              style={{
                position: 'absolute',
                top: gameState.paddle2.y,
                left: 550,
                width: 10,
                height: 50,
                backgroundColor: 'green',
              }}
            ></div>
          </div>
        </>
      )}
      <div>
        <input
          type="range"
          min="0"
          max="350"
          value={paddle1Y}
          onChange={handlePaddle1Move}
          style={{ margin: '10px' }}
        />
        <input
          type="range"
          min="0"
          max="350"
          value={paddle2Y}
          onChange={handlePaddle2Move}
          style={{ margin: '10px' }}
        />
      </div>
    </div>
  );
}

export default App;
